import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

// User types and schemas
export const insertUserSchema = z.object({
  username: z.string().min(3),
  password: z.string().min(6),
  email: z.string().email(),
  fullName: z.string().optional(),
  role: z.enum(["user", "admin"]).default("user"),
});

export type InsertUser = z.infer<typeof insertUserSchema>;

export interface User extends InsertUser {
  id: string;
  createdAt: Date;
}

// Session types and schemas
export const insertSessionSchema = z.object({
  title: z.string().min(1),
  description: z.string().min(1),
  instructor: z.string().min(1),
  date: z.coerce.date(),
  startTime: z.string(),
  endTime: z.string(),
  level: z.enum(["beginner", "intermediate", "advanced"]),
  maxParticipants: z.number().int().positive(),
  location: z.string().min(1),
});

export const updateSessionSchema = insertSessionSchema.partial();

export type InsertSession = z.infer<typeof insertSessionSchema>;

export interface Session extends InsertSession {
  id: string;
  currentParticipants: number;
  createdAt: Date;
}

// Booking types and schemas
export const insertBookingSchema = z.object({
  userId: z.string(),
  sessionId: z.string(),
  status: z.enum(["confirmed", "cancelled", "completed"]).default("confirmed"),
});

export type InsertBooking = z.infer<typeof insertBookingSchema>;

export interface Booking extends InsertBooking {
  id: string;
  createdAt: Date;
}

// Exercise types and schemas
export const insertExerciseSchema = z.object({
  name: z.string().min(1),
  description: z.string().min(1),
  category: z.enum(["flexibility", "strength", "balance", "relaxation"]),
  difficulty: z.enum(["beginner", "intermediate", "advanced"]),
  duration: z.number().int().positive(),
  videoUrl: z.string().url().optional(),
  imageUrl: z.string().url().optional(),
});

export type InsertExercise = z.infer<typeof insertExerciseSchema>;

export interface Exercise extends InsertExercise {
  id: string;
  createdAt: Date;
}

// Announcement types and schemas
export const insertAnnouncementSchema = z.object({
  title: z.string().min(1),
  content: z.string().min(1),
  authorId: z.string(),
});

export type InsertAnnouncement = z.infer<typeof insertAnnouncementSchema>;

export interface Announcement extends InsertAnnouncement {
  id: string;
  createdAt: Date;
}

