  return (
    <div className="min-h-screen">
      <Navbar />

      <div className="container mx-auto px-4 py-12">
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2">Exercise Library</h1>
          <p className="text-muted-foreground font-serif">Watch instructional videos to perfect your practice</p>
        </div>

        <div className="mb-8">
          <div className="relative max-w-full md:max-w-md">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search exercises..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
              data-testid="input-search"
            />
          </div>
        </div>

        <Tabs value={selectedLevel} onValueChange={setSelectedLevel} className="mb-8">
          <TabsList className="grid w-full grid-cols-2 sm:grid-cols-4">
            <TabsTrigger value="all" data-testid="tab-all">All Levels</TabsTrigger>
            <TabsTrigger value="Beginner" data-testid="tab-beginner">Beginner</TabsTrigger>
            <TabsTrigger value="Intermediate" data-testid="tab-intermediate">Intermediate</TabsTrigger>
            <TabsTrigger value="Advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
          </TabsList>
        </Tabs>

        {isLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">Loading exercises...</p>
          </div>
        ) : (
          <>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredExercises.map((exercise) => (
                <ExerciseCard
                  key={exercise.id}
                  {...exercise}
                  thumbnailUrl={thumbnailMap[exercise.category] || indoorImage}
                  onPlay={() => handlePlayVideo(exercise)}
                />
              ))}
            </div>

            {filteredExercises.length === 0 && !isLoading && (
              <div className="text-center py-12">
                <p className="text-muted-foreground">No exercises found matching your search</p>
              </div>
            )}
          </>
        )}
      </div>

      <VideoModal
        open={videoModal.open}
        onClose={() => setVideoModal({ open: false })}
        videoUrl={videoModal.exercise?.videoUrl}
        title={videoModal.exercise?.title}
      />

      <Footer />
    </div>
  );
}

client/src/pages/Login.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useAuth } from '@/contexts/DemoAuthContext';
import { useToast } from '@/hooks/use-toast';

export default function Login() {
  const [, setLocation] = useLocation();
  const { login, register } = useAuth();
  const { toast } = useToast();
  const [loginType, setLoginType] = useState<'user' | 'admin'>('user');
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [signupForm, setSignupForm] = useState({ 
    username: '', 
    password: '', 
    email: '', 
    fullName: '' 
  });

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    const success = await login(loginForm.username, loginForm.password);
    if (success) {
      toast({ title: 'Welcome back!', description: 'You have successfully logged in.' });
      setLocation('/');
    } else {
      toast({ 
        title: 'Login failed', 
        description: 'Invalid username or password',
        variant: 'destructive'
      });
    }
  };

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault();
    const result = await register(
      signupForm.username, 
      signupForm.password, 
      signupForm.email, 
      signupForm.fullName
    );

    if (result.success) {
      toast({ 
        title: 'Account created!', 
        description: 'Welcome to Sarvaswasthyam Yoga & Fitness!' 
      });
      setLocation('/');
    } else {
      toast({ 
        title: 'Signup failed', 
        description: result.error || 'Please try again',
        variant: 'destructive'
      });
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-primary/10 via-background to-accent/10 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="mx-auto mb-4 flex h-16 w-16 items-center justify-center rounded-full bg-primary text-primary-foreground">
            <span className="text-2xl font-bold">S</span>
          </div>
          <CardTitle className="text-2xl">Welcome</CardTitle>
          <CardDescription>Sign in or create a new account</CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="signin" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="signin">Sign In</TabsTrigger>
              <TabsTrigger value="signup">Sign Up</TabsTrigger>
            </TabsList>

            <TabsContent value="signin">
              <div className="space-y-4">
                <div className="flex gap-2 mb-4">
                  <Button
                    type="button"
                    variant={loginType === 'user' ? 'default' : 'outline'}
                    onClick={() => setLoginType('user')}
                    className="flex-1"
                  >
                    User
                  </Button>
                  <Button
                    type="button"
                    variant={loginType === 'admin' ? 'default' : 'outline'}
                    onClick={() => setLoginType('admin')}
                    className="flex-1"
                  >
                    Admin
                  </Button>
                </div>

                <form onSubmit={handleLogin} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="login-username">Username</Label>
                    <Input
                      id="login-username"
                      value={loginForm.username}
                      onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
                      placeholder="Enter your username"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="login-password">Password</Label>
                    <Input
                      id="login-password"
                      type="password"
                      value={loginForm.password}
                      onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                      placeholder="Enter your password"
                      required
                    />
                  </div>
                  <Button type="submit" className="w-full">
                    Sign In as {loginType === 'admin' ? 'Admin' : 'User'}
                  </Button>
                </form>
              </div>
            </TabsContent>

            <TabsContent value="signup">
              <form onSubmit={handleSignup} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="signup-fullname">Full Name</Label>
                  <Input
                    id="signup-fullname"
                    value={signupForm.fullName}
                    onChange={(e) => setSignupForm({ ...signupForm, fullName: e.target.value })}
                    placeholder="Enter your full name"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="signup-username">Username</Label>
                  <Input
                    id="signup-username"
                    value={signupForm.username}
                    onChange={(e) => setSignupForm({ ...signupForm, username: e.target.value })}
                    placeholder="Choose a username"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="signup-email">Email</Label>
                  <Input
                    id="signup-email"
                    type="email"
                    value={signupForm.email}
                    onChange={(e) => setSignupForm({ ...signupForm, email: e.target.value })}
                    placeholder="Enter your email"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="signup-password">Password</Label>
                  <Input
                    id="signup-password"
                    type="password"
                    value={signupForm.password}
                    onChange={(e) => setSignupForm({ ...signupForm, password: e.target.value })}
                    placeholder="Create a password"
                    required
                  />
                </div>
                <Button type="submit" className="w-full">
                  Create Account
                </Button>
              </form>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Sessions.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Navbar } from '@/components/Navbar';
import { Footer } from '@/components/Footer';
import { SessionCard } from '@/components/SessionCard';
import { BookingModal } from '@/components/BookingModal';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import type { Session } from '@shared/schema';
import indoorImage from '@assets/generated_images/Indoor_yoga_studio_session_520d01bc.png';
import meditationImage from '@assets/generated_images/Meditation_session_image_9674324d.png';
import powerYogaImage from '@assets/generated_images/Power_yoga_session_0ab6d541.png';

const imageMap: Record<string, string> = {
  'Hatha': indoorImage,
  'Meditation': meditationImage,
  'Vinyasa': powerYogaImage,
  'Restorative': indoorImage,
  'Ashtanga': powerYogaImage,
  'Yin': meditationImage,
};

export default function Sessions() {
  const [bookingModal, setBookingModal] = useState<{ open: boolean; session?: Session }>({ open: false });
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [levelFilter, setLevelFilter] = useState('all');

  const { data: sessions = [], isLoading } = useQuery<Session[]>({
    queryKey: ['/api/sessions'],
  });

  const filteredSessions = sessions.filter((session) => {
    if (categoryFilter !== 'all' && session.category !== categoryFilter) return false;
    if (levelFilter !== 'all' && session.level !== levelFilter) return false;
    return true;
  });

  const handleBookSession = (session: Session) => {
    setBookingModal({ open: true, session });
  };

  return (
    <div className="min-h-screen">
      <Navbar />

      <div className="container mx-auto px-4 py-12">
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2">Yoga Sessions</h1>
          <p className="text-muted-foreground font-serif">Browse and book from our expert-led yoga classes</p>
        </div>

        <div className="mb-8 flex flex-wrap items-center gap-4">
          <div className="space-y-2 w-full sm:w-auto">
            <Label>Category</Label>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-full sm:w-48" data-testid="select-category">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                <SelectItem value="Hatha">Hatha</SelectItem>
                <SelectItem value="Vinyasa">Vinyasa</SelectItem>
                <SelectItem value="Meditation">Meditation</SelectItem>
                <SelectItem value="Restorative">Restorative</SelectItem>
                <SelectItem value="Ashtanga">Ashtanga</SelectItem>
                <SelectItem value="Yin">Yin</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2 w-full sm:w-auto">
            <Label>Level</Label>
            <Select value={levelFilter} onValueChange={setLevelFilter}>
              <SelectTrigger className="w-full sm:w-48" data-testid="select-level">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Levels</SelectItem>
                <SelectItem value="Beginner">Beginner</SelectItem>
                <SelectItem value="Intermediate">Intermediate</SelectItem>
                <SelectItem value="Advanced">Advanced</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {(categoryFilter !== 'all' || levelFilter !== 'all') && (
            <Button
              variant="ghost"
              onClick={() => {
                setCategoryFilter('all');
                setLevelFilter('all');
              }}
              className="mt-auto"
              data-testid="button-clear-filters"
            >
              Clear Filters
            </Button>
          )}
        </div>

        {isLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">Loading sessions...</p>
          </div>
        ) : (
          <>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredSessions.map((session) => (
                <SessionCard
                  key={session.id}
                  {...session}
                  imageUrl={imageMap[session.category] || indoorImage}
                  onBook={() => handleBookSession(session)}
                />
              ))}
            </div>

            {filteredSessions.length === 0 && !isLoading && (
              <div className="text-center py-12">
                <p className="text-muted-foreground">No sessions found matching your filters</p>
              </div>
            )}
          </>
        )}
      </div>

      <BookingModal
        open={bookingModal.open}
        onClose={() => setBookingModal({ open: false })}
        sessionId={bookingModal.session?.id}
        sessionTitle={bookingModal.session?.title}
        sessionPrice={bookingModal.session?.price}
      />

      <Footer />
    </div>
  );
}

client/src/pages/UserDashboard.tsx.content:
import { Navbar } from '@/components/Navbar';
import { Footer } from '@/components/Footer';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useAuth } from '@/contexts/DemoAuthContext';
import { useQuery, useMutation } from '@tanstack/react-query';
import { mockApi } from '@/lib/mockApi';
import { useToast } from '@/hooks/use-toast';
import { Calendar, Clock, User, Mail, X } from 'lucide-react';
import type { Booking, Session } from '@shared/schema';

interface BookingWithSession extends Booking {
  session?: Session;
}

export default function UserDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();

  const { data: bookings = [], isLoading } = useQuery<Booking[]>({
    queryKey: ['/api/bookings'],
  });

  const { data: sessions = [] } = useQuery<Session[]>({
    queryKey: ['/api/sessions'],
  });

  const bookingsWithSessions: BookingWithSession[] = bookings.map((booking) => ({
    ...booking,
    session: sessions.find((s) => s.id === booking.sessionId),
  }));

  const cancelBookingMutation = useMutation({
    mutationFn: async (id: string) => {
      await mockApi.bookings.cancel(id);
    },
    onSuccess: () => {
      toast({ 
        title: 'Booking cancelled', 
        description: 'Your booking has been cancelled successfully.' 
      });
      window.location.reload();
    },
    onError: () => {
      toast({ 
        title: 'Cancellation failed', 
        description: 'Unable to cancel booking. Please try again.',
        variant: 'destructive'
      });
    },
  });

  const handleCancelBooking = (id: string) => {
    if (confirm('Are you sure you want to cancel this booking?')) {
      cancelBookingMutation.mutate(id);
    }
  };

  return (
    <div className="min-h-screen">
      <Navbar />

      <div className="container mx-auto px-4 py-12">
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2">My Dashboard</h1>
          <p className="text-muted-foreground font-serif">Manage your bookings and profile</p>
        </div>

        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6 mb-8">
          <Card>
            <CardHeader>
              <CardTitle className="text-base md:text-lg">Upcoming Sessions</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl md:text-3xl font-bold">{bookings.length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader>
              <CardTitle className="text-base md:text-lg">Total Bookings</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl md:text-3xl font-bold">{bookings.length}</div>
            </CardContent>
          </Card>
          <Card className="sm:col-span-2 lg:col-span-1">
            <CardHeader>
              <CardTitle className="text-base md:text-lg">Member Since</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-xl md:text-2xl font-bold">2024</div>
            </CardContent>
          </Card>
        </div>

        <div className="grid lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 order-2 lg:order-1">
            <Card>
              <CardHeader>
                <CardTitle>My Bookings</CardTitle>
                <CardDescription>Your upcoming yoga sessions</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {isLoading ? (
                  <p className="text-sm text-muted-foreground">Loading bookings...</p>
                ) : bookingsWithSessions.length === 0 ? (
                  <p className="text-sm text-muted-foreground">No bookings yet. Book a session to get started!</p>
                ) : (
                  bookingsWithSessions.map((booking) => (
                    <div
                      key={booking.id}
                      className="flex flex-wrap items-center justify-between gap-4 rounded-lg border p-4"
                      data-testid={`booking-${booking.id}`}
                    >
                      <div className="space-y-1 flex-1">
                        <h3 className="font-semibold">{booking.session?.title || 'Unknown Session'}</h3>
                        <p className="text-sm text-muted-foreground">{booking.session?.instructor || 'Unknown Instructor'}</p>
                        <div className="flex items-center gap-4 text-sm text-muted-foreground">
                          <div className="flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            <span>{booking.sessionDate}</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <Clock className="h-3 w-3" />
                            <span>{booking.session?.schedule || 'TBD'}</span>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant="secondary">{booking.status}</Badge>
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleCancelBooking(booking.id)}
                          disabled={cancelBookingMutation.isPending}
                          data-testid={`button-cancel-${booking.id}`}
                        >
                          <X className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  ))
                )}
              </CardContent>
            </Card>
          </div>

          <div className="order-1 lg:order-2">
            <Card>
              <CardHeader>
                <CardTitle>Profile</CardTitle>
                <CardDescription>Your account information</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center gap-3">
                  <div className="flex h-12 w-12 items-center justify-center rounded-full bg-primary text-primary-foreground">
                    <User className="h-6 w-6" />
                  </div>
                  <div>
                    <p className="font-medium">{user?.fullName || user?.username}</p>
                    <p className="text-sm text-muted-foreground">Member</p>
                  </div>
                </div>
                <div className="space-y-2 pt-4 border-t">
                  <div className="flex items-center gap-2 text-sm">
                    <Mail className="h-4 w-4 text-muted-foreground" />
                    <span>{user?.email}</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <User className="h-4 w-4 text-muted-foreground" />
                    <span>@{user?.username}</span>
                  </div>
                </div>
                <Button variant="outline" className="w-full mt-4" data-testid="button-edit-profile">
                  Edit Profile
                </Button>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

